mapper.select:
/*com.example.todaysmenu.board.repository.BoardRepository.listPaging[목록페이징]*/
SELECT TFB.TFB_SEQ , TFB.TFB_TITLE_NM , TFB.TFB_TEXT , TFB.TFB_INPUT_TY , TFB.TFB_INPUT_NM , TO_CHAR(TFB.TFB_INPUT_DT,'YYYY-MM-dd') AS TFB_INPUT_DT , TFB.TFB_INPUT_IP , TFB.TFB_MODER_TY , TFB.TFB_MODER_NM , TO_CHAR(TFB.TFB_MODER_DT,'YYYY-MM-dd') AS TFB_MODER_DT , TFB.TFB_MODER_IP FROM TM_FREE_BOARD TFB
ORDER BY TFB_SEQ DESC

mapper.update:
/*com.example.todaysmenu.board.repository.BoardRepository.update[수정]*/
UPDATE	TM_FREE_BOARD
SET		TFB_MODER_TY 	= null
,		TFB_MODER_NM 	= #{tfb_moder_nm}
,		TFB_MODER_DT 	= null
,		TFB_MODER_IP	= null
,       TFB_TITLE_NM    = #{tfb_title_nm}
,       TFB_TEXT        = #{tfb_text}
WHERE	TFB_SEQ			= #{tfb_seq}

mapper.delete:
/*com.example.todaysmenu.board.repository.BoardRepository.delete[삭제]*/
DELETE FROM	TM_FREE_BOARD
WHERE	1 = 1
AND		TFB_SEQ = #{tfb_seq}
