mapper.insert:
/*com.example.todaysmenu.common.commonFile.repository.CommonFileRepository.register[파일 업로드]*/
INSERT INTO TM_COMMON_FILE_TBL
(   TCFT_SEQ
,   TCFT_PARENT_SEQ
,   TCFT_PARENT_TABLE_TYPE
,   TCFT_ORIGIN_FILE_NAME
,   TCFT_CHANGE_FINE_NAME
,   TCFT_INPUT_TY
,   TCFT_INPUT_NM
,   TCFT_INPUT_ID
,   TCFT_INPUT_IP
,   TCFT_INPUT_DT
)
VALUES
(   TM_TCFT_SEQ.nextval
,   #{tcft_parent_seq}
,   #{tcft_parent_table_type}
,   #{tcft_origin_file_name}
,   #{tcft_change_fine_name}
,   #{tcft_input_ty}
,   #{tcft_input_nm}
,   #{tcft_input_id}
,   #{tcft_input_ip}
,   SYSDATE
)
SELECT TM_TCFT_SEQ.CURRVAL FROM DUAL

mapper.update:
/*com.example.todaysmenu.common.commonFile.repository.CommonFileRepository.memberUpdate[파일 정보 수정]*/
UPDATE TM_COMMON_FILE_TBL
SET TCFT_MODER_TY = #{tmft_moder_ty}
, TCFT_MODER_NM = #{tmft_moder_nm}
, TCFT_MODER_ID = #{tmft_moder_id}
, TCFT_MODER_IP = #{tmft_moder_ip}
, TCFT_MODER_DT = SYSDATE
, TCFT_PARENT_SEQ = #{tmft_parent_seq}
, TCFT_PARENT_TABLE_TYPE = #{tcft_parent_table_type}
, TCFT_ORIGIN_FILE_NAME = #{tmft_origin_file_name}
, TCFT_CHANGE_FINE_NAME = #{tmft_change_fine_name}
WHERE TCFT_SEQ = #{tmft_seq}

mapper.delete:
/*com.example.todaysmenu.common.commonFile.repository.CommonFileRepository.memberUpdate[회원정보수정]*/
DELETE FROM	TM_COMMON_FILE_TBL
WHERE	1 = 1
AND		TCFT_PARENT_SEQ = #{tcft_parent_seq}
