mapper.insert:
/*com.example.todaysmenu.member.repository.MemberRepository.register[회원가입]*/
INSERT INTO TM_MEMBER_TBL
(   TMT_SEQ
,   TMT_LOGIN_ID
,   TMT_PASS_WORD
,   TMT_MEMB_NAME
,   TMT_MEMB_AGE
,   TMT_MEMB_GENDER
,   TMT_MEMB_EMAIL
,   TMT_MEMB_BIRTH_DAY
,   TMT_MEMB_FILE
,   TMT_INPUT_DATE
)
VALUES
(   TM_TMT_SEQ.nextval
,   #{tmt_login_id}
,   #{tmt_pass_word}
,   #{tmt_memb_name}
,   #{tmt_memb_age}
,   #{tmt_memb_gender}
,   #{tmt_memb_email}
,   SYSDATE
,   #{tmt_memb_file}
,   SYSDATE
)

mapper.select:
/*com.example.todaysmenu.member.repository.MemberRepository.login[로그인]*/
SELECT  TMT.*
FROM    TM_MEMBER_TBL TMT
WHERE   TMT_LOGIN_ID = #{tmt_login_id}
AND     TMT_PASS_WORD = #{tmt_pass_word}
