mapper.sql:
WHERE 1 = 1

    AND	UPPER(T1.TRMT_MENU_NAME) || UPPER(T1.TRT_REST_NAME)  LIKE '%' || UPPER(#{search}) || '%'


    AND     T1.TRMT_PRICE >= #{startPriceSh}


    AND     T1.TRMT_PRICE <= #{endPriceSh}


    AND     T1.TRT_REGI = #{trt_regiSh}


    AND     T1.TRT_FOOD_TYPE = #{trt_food_typeSh}

mapper.select:
/*com.example.todaysmenu.restaurant.menu.repository.RestMenuRepository.listPaging[목록페이징]*/
SELECT T1.* , ( SELECT TRST.TRST_STAR_SCORE FROM TM_REST_STAR_TBL TRST WHERE TRST_INPUT_ID = #{tmt_login_id} AND TRST_PARENT_TYPE = 'MENU' AND TRST.TRST_PARENT_SEQ = T1.TRMT_SEQ ) AS TRST_STAR_SCORE , NVL(( SELECT ROUND(AVG(TRST_STAR_SCORE),1) || '점' FROM TM_REST_STAR_TBL TRST GROUP BY TRST_PARENT_SEQ HAVING TRST.TRST_PARENT_SEQ = T1.TRMT_SEQ ),'미평가') AS TRST_AVG_SCORE , ( SELECT TRST.TRST_SEQ FROM TM_REST_STAR_TBL TRST WHERE TRST_INPUT_ID = #{tmt_login_id} AND TRST_PARENT_TYPE = 'MENU' AND TRST.TRST_PARENT_SEQ = T1.TRMT_SEQ ) AS TRST_SEQ , NVL(( SELECT TO_CHAR(TRST.TRST_TODAY_YMD,'YYYY-MM-dd') FROM TM_REST_STAR_TBL TRST WHERE TRST_INPUT_ID = #{tmt_login_id} AND TRST_PARENT_TYPE = 'MENU' AND TRST.TRST_PARENT_SEQ = T1.TRMT_SEQ ),'안먹어본 메뉴') AS TRST_TODAY_YMD FROM( SELECT TRMT.TRT_SEQ , TRMT.TRMT_SEQ , TRMT.TRMT_MENU_NAME , TO_CHAR(TRMT.TRMT_PRICE,'999,999,999,999,999') || '원' AS TRMT_PRICE_COMMA , TRMT.TRMT_MENU_TEXT , TRT.TRT_REST_NAME , TRT.TRT_REGI , TRT.TRT_ADDR , TRT.TRT_FOOD_TYPE FROM TM_REST_MENU_TBL TRMT LEFT JOIN TM_REST_TBL TRT ON TRMT.TRT_SEQ = TRT.TRT_SEQ ) T1
ORDER BY TRMT_SEQ DESC

mapper.insert:
/*com.example.todaysmenu.restaurant.menu.repository.RestMenuRepository.insert[등록]*/
INSERT INTO TM_REST_MENU_TBL
(   TRMT_SEQ
,   TRT_SEQ
,   TRMT_MENU_NAME
,   TRMT_PRICE
,   TRMT_MENU_TEXT
,   TRMT_INPUT_TY
,   TRMT_INPUT_NM
,   TRMT_INPUT_ID
,   TRMT_INPUT_IP
,   TRMT_INPUT_DT
,   TRMT_MODER_TY
,   TRMT_MODER_NM
,   TRMT_MODER_ID
,   TRMT_MODER_IP
,   TRMT_MODER_DT
)
VALUES
(   TM_TRMT_SEQ.nextval
,   #{trt_seq}
,   #{trmt_menu_name}
,   #{trmt_price}
,   #{trmt_menu_text}
,   #{trmt_input_ty}
,   #{trmt_input_nm}
,   #{trmt_input_id}
,   #{trmt_input_ip}
,   SYSDATE
,   #{trmt_moder_ty}
,   #{trmt_moder_nm}
,   #{trmt_moder_id}
,   #{trmt_moder_ip}
,   SYSDATE
)
SELECT TM_TRMT_SEQ.CURRVAL FROM DUAL

mapper.update:
/*com.example.todaysmenu.restaurant.menu.repository.RestMenuRepository.update[수정]*/
UPDATE	TM_REST_MENU_TBL
SET		TRMT_MODER_TY 	    =         #{trmt_moder_ty}
,		TRMT_MODER_NM 	    =         #{trmt_moder_nm}
,		TRMT_MODER_ID 	    =         #{trmt_moder_id}
,		TRMT_MODER_IP	    =         #{trmt_moder_ip}
,		TRMT_MODER_DT	    =         SYSDATE
,       TRMT_MENU_NAME      =         #{trmt_menu_name}
,       TRMT_PRICE          =         #{trmt_price}
,       TRMT_MENU_TEXT      =         #{trmt_menu_text}
WHERE	TRMT_SEQ			=         #{trmt_seq}

mapper.delete:
/*com.example.todaysmenu.restaurant.menu.repository.RestMenuRepository.delete[삭제]*/
DELETE FROM	TM_REST_MENU_TBL
WHERE	1 = 1
AND		TRMT_SEQ = #{trmt_seq}

/*com.example.todaysmenu.restaurant.menu.repository.RestMenuRepository.delete[삭제]*/
DELETE FROM	TM_REST_MENU_TBL
WHERE	1 = 1
AND		TRT_SEQ = #{trt_seq}
